import React, { Component } from 'react';
import "./TaskComponent.css";
import { useState, useEffect, useRef} from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrashCan } from '@fortawesome/free-regular-svg-icons';
import { motion, AnimatePresence } from 'framer-motion';



function TaskComponent(){
    const [tasks, setTasks] = useState(() => {
        const storedTasks = localStorage.getItem('tasks');
        return storedTasks ? JSON.parse(storedTasks) : [];
      });
    const [newTask, setNewTask] = useState('');


    //---useEFFECT HOOK TO LOAD TASKS FROM LOCALSTORAGE ON PAGE LOAD---
    useEffect(() => {
        try {
          const storedTasks = localStorage.getItem('tasks');
          if (storedTasks) {
            setTasks(JSON.parse(storedTasks));
            console.log('Tasks loaded successfully:', JSON.parse(storedTasks));
          } else {
            console.log('No saved tasks found.');
          }
        } catch (error) {
          console.error('Error loading tasks from localStorage:', error);
        }
      }, []);


    //---useEFFECT HOOK TO SAVE TASKS TO LOCALSTORAGE ON CHANGE---
    useEffect(() => {
        localStorage.setItem('tasks', JSON.stringify(tasks));
    }, [tasks]);


    //---useEFFECT HOOK TO TRACK BUTTON-CLICKS ANYWHERE ON THE DOCUMENT---
    useEffect(() => {
        document.addEventListener('keydown', handleKeyDown);
        return () => {
          document.removeEventListener('keydown', handleKeyDown);
        };
      }, [newTask, tasks]);



    //---FUNCTION THAT HANDLES KEY PRESSES IN INPUT FIELD---
    const handleKeyDown = (e) => {
        if (e.key === 'Enter') {
          e.preventDefault();
          addTask();
        }
    };

     //---FUNCTION THAT HANDLES ADDING NEW TASK TO LIST/ADD BUTTON CLICK---
    const addTask = () => {
        if (newTask.trim() !== '') {
            setTasks((prevTasks) => [
                ...prevTasks,
                { id: Date.now(), text: newTask, completed: false },
            ]);
            setNewTask('');
        }
    };

    //---FUNCTION THAT HANDLES TOGGLING INPUT FIELD CLICK---
    const toggleTask = (taskId) => {
        setTasks(
            tasks.map((task) =>
            task.id === taskId ? { ...task, completed: !task.completed } : task
            )
        );
    };

    //---FUNCTION THAT HANDLES DELETING TASK FROM LIST---
    const deleteTask = (taskId) => {
        setTasks((prevTasks) => prevTasks.filter((task) => task.id !== taskId));
    };




    return (
    <div className="App"> 
     
      <motion.div initial={{opacity:0, transition: { duration:0.2, ease: "easeInOut" }}} animate={{opacity:1, transition: { duration:0.2, ease: "easeInOut" }}} exit={{opacity:0, transition: { duration:0.2, ease: "easeInOut" }}} className={`container-fluid my-body`}>
        <div className="add-task">
            <input
            id="100000"
            type="text"
            placeholder="New task to-do"
            value={newTask}
            onChange={(e) => setNewTask(e.target.value)}
            />
            <div className="btn btn-outline-primary" type="submit" onClick={addTask}>Add</div>
        </div>
    </motion.div>
        <AnimatePresence>
        <motion.ul  key="task-list"
                    className="task-list"
                    initial={{ opacity: 1 }}
                    exit={{ opacity: 0, y: -20, transition: { duration: 0.2 } }}
                    transition={{ opacity: { duration: 0.8 } }} >
            <AnimatePresence>
            {tasks.map((task,index) => (

                <motion.li  key={task.id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                            transition={{ duration:1, layoutY: { type: 'spring', stiffness: 500, damping: 30 } }}>
                    <motion.div initial={{opacity:0, x:"-100%", transition: { duration:0.3, ease: "easeInOut" }}} animate={{opacity:1,x:"0", transition: { duration:0.8, ease: "easeInOut" }}} exit={{opacity:0,x:"100%", transition: { duration:0.6, ease: "easeInOut" }}} className={`container-fluid col-9 col-sm-9`}>
                        <div className="form-check" key={task.id}>
                            <input id={task.id} className="form-check-input" type="checkbox" checked={task.completed} value="" onChange={() => toggleTask(task.id)}/>
                            <label className={`text-decoration form-check-label ${task.completed ? 'completed' : ''}`} htmlFor={task.id}>
                                {task.text}
                            </label>
                        </div>
                    </motion.div>
                    <motion.div initial={{opacity:0, x:"100%", transition: { duration:0.2, ease: "easeInOut" }}} animate={{opacity:1,x:"0", transition: { duration:0.8, ease: "easeInOut" }}} exit={{opacity:0, transition: { duration:0.2, ease: "easeInOut" }}} className={`container-fluid col-sm-2`}>
                        <div className="btn btn-sm btn-outline-danger" onClick={() => deleteTask(task.id)}>Delete <FontAwesomeIcon icon={faTrashCan} /></div>
                    </motion.div>
                </motion.li>

            ))}
            </AnimatePresence>
        </motion.ul>
        </AnimatePresence>

      


    </div>

    );
    
  }
export default TaskComponent;
