import React, { Component } from 'react';
import "./TaskComponent.css";
import {motion} from 'framer-motion';
import { useState, useEffect, useRef} from 'react';


function TaskComponent(){
    const [tasks, setTasks] = useState(() => {
        const storedTasks = localStorage.getItem('tasks');
        return storedTasks ? JSON.parse(storedTasks) : [];
      });
    const [newTask, setNewTask] = useState('');

    useEffect(() => {
        try {
          const storedTasks = localStorage.getItem('tasks');
          if (storedTasks) {
            setTasks(JSON.parse(storedTasks));
            console.log('Tasks loaded successfully:', JSON.parse(storedTasks));
          } else {
            console.log('No saved tasks found.');
          }
        } catch (error) {
          console.error('Error loading tasks from localStorage:', error);
        }
      }, []);

      useEffect(() => {
        localStorage.setItem('tasks', JSON.stringify(tasks));
    }, [tasks]);


    const addTask = () => {
        if (newTask.trim() !== '') {
            setTasks((prevTasks) => [
                ...prevTasks,
                { id: Date.now(), text: newTask, completed: false },
            ]);
            setNewTask('');
        }
    };

    const toggleTask = (taskId) => {
        setTasks(
            tasks.map((task) =>
            task.id === taskId ? { ...task, completed: !task.completed } : task
            )
        );
    };

    const deleteTask = (taskId) => {
        setTasks(tasks.filter((task) => task.id !== taskId));
    };




    return (
    <div className="App"> 
     
      <motion.div initial={{opacity:0, transition: { duration:0.2, ease: "easeInOut" }}} animate={{opacity:1, transition: { duration:0.2, ease: "easeInOut" }}} exit={{opacity:0, transition: { duration:0.2, ease: "easeInOut" }}} className={`container-fluid my-body`}>

    
        <div className="add-task">
            <input
            id="100000"
            type="text"
            placeholder="Add a new task"
            value={newTask}
            onChange={(e) => setNewTask(e.target.value)}
            />
            <div className="btn btn-outline-primary" onClick={addTask}>Add</div>
        </div>
        <ul className="task-list">
        {tasks.map((task) => (

          <li key={task.id}>
            <motion.div initial={{opacity:0, x:"-100%", transition: { duration:0.2, ease: "easeInOut" }}} animate={{opacity:1,x:"0", transition: { duration:0.8, ease: "easeInOut" }}} exit={{opacity:0,x:"100%", transition: { duration:0.2, ease: "easeInOut" }}} className={`container-fluid col-10`}>
                <div className="form-check" key={task.id}>
                    <input id={task.id} className="form-check-input" type="checkbox" value="" onChange={() => toggleTask(task.id)}/>
                    <label className={`form-check-label ${task.completed ? 'completed' : ''}`} htmlFor={task.id}>
                        {task.text}
                    </label>
                </div>
            </motion.div>
            <motion.div initial={{opacity:0, x:"100%", transition: { duration:0.2, ease: "easeInOut" }}} animate={{opacity:1,x:"0", transition: { duration:0.8, ease: "easeInOut" }}} exit={{opacity:0,x:"100%", transition: { duration:0.2, ease: "easeInOut" }}} className={`container-fluid col-2`}>
                <div className="btn btn-outline-danger" onClick={() => deleteTask(task.id)}>Delete</div>
            </motion.div>
          </li>

        ))}

      </ul>



      </motion.div>
      


    </div>

    );
    
  }
export default TaskComponent;
