import React, { Component } from 'react';
import "./TaskComponent.css";
import {motion} from 'framer-motion';
import { useState, useEffect, useRef} from 'react';


function TaskComponent(){
    const [tasks, setTasks] = useState([]);
    const [newTask, setNewTask] = useState('');
    const addTask = () => {
        if (newTask.trim() !== '') {
          setTasks([...tasks, { id: Date.now(), text: newTask, completed: false }]);
          setNewTask('');
        }
      };
    
      const toggleTask = (taskId) => {
        setTasks(
          tasks.map((task) =>
            task.id === taskId ? { ...task, completed: !task.completed } : task
          )
        );
      };
    
      const deleteTask = (taskId) => {
        setTasks(tasks.filter((task) => task.id !== taskId));
      };
    return (
    <div> 
     
      <motion.div initial={{opacity:0, transition: { duration:0.2, ease: "easeInOut" }}} animate={{opacity:1, transition: { duration:0.2, ease: "easeInOut" }}} exit={{opacity:0, transition: { duration:0.2, ease: "easeInOut" }}} className={`container-fluid my-body`}>
        
        <motion.div initial={{opacity:0, transition: { duration:0.2, ease: "easeInOut" }}} animate={{opacity:1, transition: { duration:0.2, ease: "easeInOut" }}} exit={{opacity:0, transition: { duration:0.2, ease: "easeInOut" }}} className={`flex my-body`}>
            <div className="btn btn-primary col-lg-3 col-md-2 fw-light text-decoration m-1">Button 1</div>
            <div className="btn btn-primary col-lg-2 col-md-2 fw-light text-decoration ms-5">Button 2</div>
            <div className="btn btn-primary col-lg-2 col-md-2 fw-light text-decoration m-1">Button 3</div>
        </motion.div>

        <div className="App">
    
      <div className="add-task">
        <input
          type="text"
          placeholder="Add a new task"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
        />
        <div className="btn btn-primary" onClick={addTask}>Add</div>
      </div>
      <ul className="task-list">
        {tasks.map((task) => (
          <li key={task.id}>
            <input
              type="checkbox"
              checked={task.completed}
              onChange={() => toggleTask(task.id)}
            />
            <span className={task.completed ? 'completed' : ''}>
              {task.text}
            </span>
            <button onClick={() => deleteTask(task.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>



      </motion.div>
      


    </div>

    );
    
  }
export default TaskComponent;
